mapper.select:
select * from (select zzz.*, rownum||'' RNUM from (
select rac.reserve_apply_car_number||'' as "reserve_apply_car_number", to_char(rac.reservation_date,'yyyy')||'년 '||to_char(rac.reservation_date,'mm')||'월 '||to_char(rac.reservation_date,'dd')||'일 '||to_char(rac.reservation_date,'hh24')||'시' as "reservation_date", rac.reserve_count||'' as "reserve_count", rac.user_no||'' as "user_no", (select u.user_name from disability_user u where u.user_no=rac.user_no)||'' as "user_name", rac.start_road_addr||'' as "start_road_addr", rac.start_detail_addr||'' as "start_detail_addr", rac.end_road_addr||'' as "end_road_addr", rac.end_detail_addr||'' as "end_detail_addr" from reserve_apply_car rac where rac.driver_no =(select d.driver_no from driver d where driver_id=#{id}) and reserve_status_code='2' and rac.reservation_date > sysdate
) zzz) where RNUM>= ${selectPageNo} * 5 - 5+1 and RNUM <= ${selectPageNo} * 5
