mapper.select:
select * from (select zzz.*, rownum||'' RNUM from (
select RESERVE_APPLY_CAR_NUMBER as "reserve_apply_car_number" ,to_char( RESERVATION_DATE, 'YYYYMMDD' ) as "reservation_date" ,RESERVE_COUNT as "reserve_count" ,USER_NO as "user_no" ,(select user_name from DISABILITY_USER where user_no=(select user_no from RESERVE_APPLY_CAR where driver_no=(select driver_no from driver where driver_id=#{id}) and reserve_apply_car_number=(select reserve_apply_car_number from RESERVE_ACCEPT_LIST where driver_no=(select driver_no from driver where driver_id=#{id}) and RESERVE_STATUS_CODE='2') ) ) as "user_name" ,START_ROAD_ADDR as "start_road_addr" ,START_DETAIL_ADDR as "start_detail_addr" ,END_ROAD_ADDR as "end_road_addr" ,END_DETAIL_ADDR as "end_detail_addr" ,to_char( RESERVATION_DATE, 'HH24:MI:SS' ) as "reservation_hour" from RESERVE_APPLY_CAR where driver_no=(select driver_no from driver where driver_id=#{id}) and RESERVE_STATUS_CODE='2'
) zzz) where RNUM>= ${selectPageNo} * 10 - 10+1 and RNUM <= ${selectPageNo} * 10
