mapper.select:
select distinct(rac.reserve_apply_car_number||'') as "reserve_apply_car_number" ,to_char(rac.reservation_date,'yyyy-mm-dd AM HH:MI:SS') as "reservetion_date" ,rac.start_point_longitude||'' as "start_point_longitude" ,rac.start_point_latitude||'' as "start_point_latitude" ,rac.end_point_longitude||'' as "end_point_longitude" ,rac.end_point_latitude||'' as "end_point_latitude" ,rac.car_number||'' as "car_number" ,rac.driver_no||'' as "driver_no" ,rac.user_no||'' as "user_no" ,rac.reserve_count||'' as "reserve_count" ,rac.start_road_addr||'' as "start_road_addr" ,rac.start_detail_addr||'' as "start_detail_addr" ,rac.end_road_addr||'' as "end_road_addr" ,rac.end_detail_addr||'' as "end_derail_addr" ,rac.reserve_status_code||'' as "reserve_status_code" ,to_char(rac.drive_start_time,'yyyy-mm-dd AM HH:MI:SS') as "drive_start_time" ,to_char(rac.drive_finish_time,'yyyy-mm-dd AM HH:MI:SS') as "drive_finish_time" ,rac.drive_distance||'' as "drive_distance" from reserve_apply_car rac left outer join RESERVE_ACCEPT_LIST ral on rac.reserve_status_code=ral.reserve_status_code where rac.reserve_status_code=#{status_code}
and rac.driver_no=#{driver_no}
and rac.reserve_apply_car_number=ANY(select reserve_apply_car_number from RESERVE_ACCEPT_LIST where driver_no=#{driver_no} and reserve_status_code!=3 )

mapper.update:
		update RESERVE_APPLY_CAR set
				
					reserve_status_code = ${parameter}
				
				
				
					,driver_no=#{driver_no}
					,car_number=(select car_number from car_info where car_driver=#{driver_no})	
				

				
					,drive_start_time=to_date( #{start_time}, 'YYYY-MM-DD HH24:MI:SS' )	
				
					
				
					
						,reserve_count=0
					
					
						reserve_count=reserve_count-1
					
				
				
		where reserve_apply_car_number=${select_num}

update RESERVE_ACCEPT_LIST set
	reserve_status_code=${parameter} 	
where reserve_apply_car_number=${select_num}
 
	and driver_no=${driver_no}

update  reserve_apply_car  set 
	reserve_status_code=${parameter} 			
	,drive_finish_time=to_date( #{endtime}, 'YYYY-MM-DD HH24:MI:SS' )
	,drive_distance=${drive_distance}||' Meter'	
where reserve_apply_car_number=${select_num}
